import {useEffect, useState} from "react"
import useSWR from "swr"
import {jsonResponseFetcher} from "./swr"

const useNewUser = params => {
  const {mutate} = useSWRConfig()
  const [data, setData] = useState()
  const [error, setError] = useState()

  useEffect(() => {
    setData(undefined)
    setError(undefined)
    //Sends request to db, then updates based on userID
    jsonResponseFetcher("/user", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(params),
    })
      .then(user => {
        setData(user)
        mutate(`/users/{user.id}`, user, false)
      })
      .catch(setError)
  }, [params])

  return {
    data,
    error,
    loading: data == null && error == null,
  }
}

function usePostRequest(postPath, postParams, keyFn) {}

usePostRequest("/user", {params}, user => `/users/{user.id}`)
