import {useEffect, useState} from "react"
import useSWR from "swr"
import {jsonResponseFetcher} from "./swr"

const useNewUser = params =>
  usePostRequest("/user", params, user => `/users/{user.id}`)

function usePostRequest(postPath, body, keyFn) {
  const {mutate} = useSWRConfig()
  const [data, setData] = useState()
  const [error, setError] = useState()

  useEffect(() => {
    setData(undefined)
    setError(undefined)
    //Sends request to db, then updates based on userID
    jsonResponseFetcher(postPath, {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(body),
    })
      .then(user => {
        setData(user)
        mutate(`/users/{user.id}`, user, false)
      })
      .catch(setError)
  }, [body])

  return {
    data,
    error,
    loading: data == null && error == null,
  }
}
