import {useEffect, useState} from "react"
import useSWR from "swr"
import {jsonResponseFetcher} from "./swr"

const useNewUser = body =>
  usePostRequest("/user", body, user => `/users/{user.id}`)

const usePostRequest = (pathname, body, keyFn) => {
  const {mutate} = useSWRConfig()
  useSWR()

  const [data, setData] = useState()
  const [error, setError] = useState()

  useEffect(() => {
    setData(undefined)
    setError(undefined)
    //Sends request to db, then updates based on userID
    jsonResponseFetcher(pathname, {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(body),
    })
      .then(data => {
        setData(data)
        mutate(keyFn(data), data, false)
      })
      .catch(setError)
  }, [body])

  return {
    data,
    error,
    loading: data == null && error == null,
  }
}
